oled_rotation_t oled_init_user(oled_rotation_t rotation) {
    // if (!is_keyboard_left()) return OLED_ROTATION_270;
    if (is_keyboard_master()) {
        if (is_keyboard_left())
        {
            return rotation;
        } else {
            return OLED_ROTATION_180;
        }
    };
    // return OLED_ROTATION_180;
    return OLED_ROTATION_270;
}

